    private void formWindowActivated(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowActivated
        // TODO add your handling code here:
        int q = 0;
        while(q<=365)
        {
            jcmbNoOfDays.addItem(q + "\n");
            q++;
        }
        
        for(int i =0; i<12; i++)
        {
            jcmbSetDiscount.addItem(i + "\n");
            i++;
        }
        
        // CREDIT LIMIT
         for(int p =0; p<=50; p++)
        {
            jcmbCreditLimit.addItem(p + "\n");
            p++;
        }
    }//GEN-LAST:event_formWindowActivated

    private void jcmbDescriptionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jcmbDescriptionActionPerformed
        // PRODUCT DESCRIPTION..
        if(jcmbDescription.getSelectedItem().equals("LawnMover"))
        {
            jlblProductCode.setText("LAWN89");
            jlblCostPerDay.setText("25000");
        }
        else if(jcmbDescription.getSelectedItem().equals("Pickup  Van"))
        {
            jlblProductCode.setText("PVAN19");
            jlblCostPerDay.setText("5000");
        }
        
        else if(jcmbDescription.getSelectedItem().equals("Cement Mixer"))
        {
            jlblProductCode.setText("CMIX29");
            jlblCostPerDay.setText("1700");
        }
        
         else if(jcmbDescription.getSelectedItem().equals("Elec. Generator"))
        {
            jlblProductCode.setText("ELGEN29");
            jlblCostPerDay.setText("8000");
        }
        
    }//GEN-LAST:event_jcmbDescriptionActionPerformed

