    private void jbtnTotalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnTotalActionPerformed
        // TODO add your handling code here:
        double Discount,SubTotal,Tax,Total;
        double CostPerDay = Double.parseDouble(jlblCostPerDay.getText());
        double SetDueDay = Double.parseDouble(jtxtSetDueDay.getText());
        Object NoSelected = jcmbNoOfDays.getSelectedItem();
        String NoOfDay = NoSelected.toString();
        double daysSelected = Double.parseDouble(NoOfDay);
        Discount =((CostPerDay * SetDueDay)*daysSelected)/100;
        SubTotal = ((CostPerDay * SetDueDay) + Discount);
        Tax  = SubTotal * 0.2;
        Total = (Tax  + SubTotal);
        
        
        String iSubT = String.format(" â‚¹%.2f", SubTotal);
        jlblSubTotal.setText(iSubT);
        
        String iTax = String.format(" â‚¹%.2f", Tax);
        jlblTax.setText(iTax);
        
        String iTotal = String.format(" â‚¹%.2f", Total);
        jlblTotal.setText(iTotal);
        
        
//        ================RECEPIT================
        double Qty1 = Double.parseDouble(jlblCostPerDay.getText());
        double Qty2 = Double.parseDouble(jtxtSetDueDay.getText());
        double Qty3 = Double.parseDouble(NoOfDay);
        double Qty4 = Double.parseDouble(jlblProductCode.getText());
        jtxtRecepit.append("""
                           \tInventory Management System
                           
                           
                           Product Code:\t\t\t """+ Qty4+"\nNumber Of Days:\t\t"
        +Qty3+"\nCost Per Day : \t\t\t"+Qty1+"Set Due Date:\t\t\t"+Qty2+"\nTax:\t\t\t"+iTax+"\n"+"Sub Total:\t\t\t"+iSubT+
                "\n\n"+"Total:\t\t\t"+iTotal+"\n\n\tPlease keep the Recepit for your Record!!");
        
    }//GEN-LAST:event_jbtnTotalActionPerformed

